// frontend/src/services/api.js
import axios from 'axios';

const apiBaseUrl = 'http://127.0.0.1:8000/';

// Servicio para subir audio
export const uploadAudio = async (audioFile) => {
    const formData = new FormData();
    formData.append('file', audioFile);

    const response = await axios.post(`${apiBaseUrl}/upload/`, formData, {
        headers: {
            'Content-Type': 'multipart/form-data',
        },
    });
    return response.data; // Debería retornar el ID del archivo
};

// Servicio para identificar el instrumento
export const identifyInstrument = async (audioId) => {
    const response = await axios.get(`${apiBaseUrl}/identify/${audioId}/`);
    return response.data; // Debería retornar el resultado del análisis
};
