// frontend/src/components/AudioUploader.js
import React, { useState } from 'react';
import { identifyInstrument } from '../Services/Api';
import './AudioUploader.css';

const AudioUploader = ({ onInstrumentIdentified }) => {
    const [audioFile, setAudioFile] = useState(null);
    const [isUploading, setIsUploading] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        if (!file) {
            setErrorMessage('Por favor selecciona un archivo.');
            return;
        }
        if (!file.type.startsWith('audio/')) {
            setErrorMessage('El archivo debe ser un archivo de audio válido.');
            return;
        }
        setErrorMessage('');
        setAudioFile(file);
    };

    const handleFileUpload = async (file) => {
        try {
            const formData = new FormData();
            formData.append("file", file);

            const response = await fetch("http://127.0.0.1:8000/api/upload/", {
                method: "POST",
                body: formData,
            });

            if (!response.ok) {
                throw new Error("Error en la respuesta del servidor");
            }

            const data = await response.json();

            if (data.predicted_class !== undefined && data.probabilities) {
                console.log("Resultados:", data);
                setResults({
                    class: data.predicted_class,
                    probabilities: data.probabilities,
                });
            } else {
                throw new Error("No se encontraron resultados");
            }
        } catch (error) {
            console.error("Error al procesar el archivo:", error);
            setErrorMessage(error.message);
        }
    };



    return (
        <div className="audio-uploader">
            <input type="file" onChange={handleFileChange} />
            <button onClick={handleUpload} disabled={isUploading}>
                {isUploading ? 'Procesando...' : 'Subir y Clasificar'}
            </button>
            {errorMessage && <p className="error-message">{errorMessage}</p>}
        </div>
    );
};

export default AudioUploader;
