// frontend/src/components/AudioUploader.js
const handleUpload = async () => {
    if (!audioFile) {
        setErrorMessage('Por favor selecciona un archivo antes de subir.');
        return;
    }

    setIsUploading(true);
    try {
        // Subir el archivo
        const uploadResponse = await uploadAudio(audioFile);
        const audioId = uploadResponse.id;

        // Identificar el instrumento
        const identifyResponse = await identifyInstrument(audioId);

        // Pasar los resultados al componente padre
        onInstrumentIdentified(identifyResponse);
    } catch (error) {
        setErrorMessage('Error procesando el archivo.');
        console.error(error);
    } finally {
        setIsUploading(false);
    }
};
