// frontend/src/components/Results.js
import React from 'react';
import './Results.css';

const Results = ({ result }) => {
    if (!result) {
        return null; // No mostrar nada si no hay resultados
    }

    return (
        <div className="results-tab">
            <h4>Resultados del Modelo</h4>
            <p><strong>Instrumento Detectado:</strong> {result.instrument}</p>
            {result.probabilities && (
                <>
                    <h5>Probabilidades:</h5>
                    <ul>
                        {result.probabilities.map((prob, index) => (
                            <li key={index}>
                                Instrumento {index + 1}: {(prob * 100).toFixed(2)}%
                            </li>
                        ))}
                    </ul>
                </>
            )}
        </div>
    );
};

export default Results;
