// frontend/src/Utils/ErrorHandlers.js

export const handleApiError = (error) => {
    if (error.response) {
        // üî• El servidor respondi√≥ con un c√≥digo fuera del rango 2xx
        console.error('üî¥ Error de respuesta del servidor:');
        console.error('üì¶ Data:', error.response.data);
        console.error('üìä Status:', error.response.status);
        console.error('üìÅ Headers:', error.response.headers);

        // üß† Si quieres devolver un mensaje espec√≠fico para mostrar en UI
        if (error.response.status === 500) {
            return 'Error interno del servidor. Por favor intenta m√°s tarde.';
        }
        if (error.response.status === 400) {
            return 'Solicitud incorrecta. Revisa los datos enviados.';
        }
    } else if (error.request) {
        // üö® La solicitud fue hecha pero no hubo respuesta
        console.error('üî¥ El servidor no respondi√≥:');
        console.error('üì° Request:', error.request);
        return 'No se pudo conectar con el servidor.';
    } else {
        // ‚ùå Error durante la configuraci√≥n de la solicitud
        console.error('‚ùå Error inesperado en la solicitud:', error.message);
        return 'Error inesperado. Intenta nuevamente.';
    }

    console.error('üß© Configuraci√≥n usada:', error.config);
    return 'Ocurri√≥ un error al procesar la solicitud.';
};
