// Sidebar.js
import React, { useState } from 'react';
import './Sidebar.css';

const Sidebar = ({ onFilterChange, onSearch, onMLAction }) => {
    const [searchQuery, setSearchQuery] = useState('');

    return (
        <div className="sidebar" style={{ width: '25%' }}>
            <div className="search-section">
                <input
                    type="text"
                    placeholder="Buscar instrumento..."
                    className="search-input"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                />
                <button
                    className="andean-button"
                    onClick={() => onSearch(searchQuery)}
                >
                    Buscar
                </button>
            </div>

            <div className="filters-section">
                <h3>Filtrar por tipo:</h3>
                <select onChange={(e) => onFilterChange(e.target.value)}>
                    <option value="all">Todos</option>
                    <option value="viento">Viento</option>
                    <option value="cuerda">Cuerda</option>
                    <option value="percusion">Percusión</option>
                </select>
            </div>

            <div className="ml-techniques-section">
                <h3>Técnicas de ML</h3>
                <button className="andean-button" onClick={() => onMLAction('clasificacion')}>
                    Clasificación
                </button>
                <button className="andean-button" onClick={() => onMLAction('identificacion')}>
                    Identificación
                </button>
            </div>
        </div>
    );
};

export default Sidebar;
