// Sidebar.js
// Sidebar.js
import React from 'react';
import './Sidebar.css';

const Sidebar = ({ onFilterChange, onMLAction, onSearch }) => {
    let searchQuery = '';

    const handleSearchChange = (e) => {
        searchQuery = e.target.value;
    };

    return (
        <div className="sidebar" style={{ width: '25%' }}>
            {/* Sección de búsqueda */}
            <div className="search-section">
                <input
                    type="text"
                    placeholder="Buscar instrumento..."
                    className="search-input"
                    onChange={handleSearchChange}
                />
                <button
                    className="andean-button"
                    onClick={() => onSearch(searchQuery)}
                >
                    Buscar
                </button>
            </div>

            {/* Filtros por tipo */}
            <div className="filters-section">
                <h3>Filtrar por tipo:</h3>
                <select onChange={(e) => onFilterChange(e.target.value)}>
                    <option value="all">Todos</option>
                    <option value="viento">Viento</option>
                    <option value="cuerda">Cuerda</option>
                    <option value="percusion">Percusión</option>
                </select>
            </div>

            {/* Botones de instrumentos */}
            <div className="instrument-section">
                <h3>Instrumentos Andinos</h3>
                <button
                    className="andean-button"
                    onClick={() => onFilterChange('viento')}
                >
                    Viento
                </button>
                <button
                    className="andean-button"
                    onClick={() => onFilterChange('cuerda')}
                >
                    Cuerda
                </button>
                <button
                    className="andean-button"
                    onClick={() => onFilterChange('percusion')}
                >
                    Percusión
                </button>
            </div>

            {/* Botones de técnicas de ML */}
            <div className="ml-techniques-section">
                <h3>Técnicas de ML</h3>
                <button
                    className="andean-button"
                    onClick={() => onMLAction('clasificacion')}
                >
                    Clasificación
                </button>
                <button
                    className="andean-button"
                    onClick={() => onMLAction('identificacion')}
                >
                    Identificación
                </button>
            </div>
        </div>
    );
};

export default Sidebar;
