// frontend/src/components/FeedbackForm.js
import React, { useState } from 'react';
import './FeedbackForm.css';

const FeedbackForm = () => {
    const [feedback, setFeedback] = useState('');
    const [statusMessage, setStatusMessage] = useState('');
    const [statusType, setStatusType] = useState(''); // success | error

    const handleSubmit = (e) => {
        e.preventDefault();

        if (!feedback.trim()) {
            setStatusMessage('Por favor escribe tu comentario antes de enviarlo.');
            setStatusType('error');
            return;
        }

        // ðŸ”¥ AquÃ­ irÃ­a una solicitud POST si decides enviar esto al backend
        // Por ahora simulamos Ã©xito
        setStatusMessage('Â¡Gracias por tu comentario! âœ…');
        setStatusType('success');
        setFeedback('');
    };

    return (
        <div className="feedback-form">
            <h3>ðŸ“£ Tu opiniÃ³n es valiosa</h3>
            <form onSubmit={handleSubmit}>
                <textarea
                    value={feedback}
                    onChange={(e) => setFeedback(e.target.value)}
                    placeholder="Comparte tus pensamientos sobre los instrumentos andinos..."
                    rows="4"
                    required
                />
                <button type="submit" className="andean-button">Enviar Comentario</button>
            </form>

            {statusMessage && (
                <p className={`status-message ${statusType}`}>{statusMessage}</p>
            )}
        </div>
    );
};

export default FeedbackForm;
