// frontend/src/App.js
import React, { useState, useRef } from 'react';
import Header from './Components/Header';
import Sidebar from './Components/Sidebar';
import AudioUploader from './Components/AudioUploader';
import InstrumentDisplay from './Components/InstrumentDisplay';
import InstrumentInfo from './Components/InstrumentInfo';
import FeedbackForm from './Components/FeedbackForm';
import History from './Components/History';
import Results from './Components/Results';
import Footer from './Components/Footer';
import './App.css';

const App = () => {
    // eslint-disable-next-line no-unused-vars
    const [filter, setFilter] = useState('all');
    // Estado para el filtro
    const [results, setResults] = useState(null); // Estado para los resultados
    // eslint-disable-next-line no-unused-vars
    const [identifiedInstrument, setIdentifiedInstrument] = useState(null); // Estado para el instrumento identificado
    const [sidebarOpen, setSidebarOpen] = useState(false); // Estado para la barra lateral

    // üîë Referencias para hacer scroll autom√°tico
    const refs = {
        quena: useRef(null),
        sikus: useRef(null),
        toyo: useRef(null),
        ocarina: useRef(null),
        charango: useRef(null),
        guitarra: useRef(null),
        bandola: useRef(null),
        tiple: useRef(null),
        bombo: useRef(null),
        cajon: useRef(null),
        wankara: useRef(null),
        guacharaca: useRef(null),
    };

    // üéØ L√≥gica para manejar b√∫squeda
    const handleSearch = (query) => {
        const key = query.toLowerCase().trim();
        if (refs[key]) {
            refs[key].current?.scrollIntoView({ behavior: 'smooth', block: 'center' });
        } else {
            alert(`No se encontr√≥ informaci√≥n sobre "${query}"`);
        }
    };

    const handleFilterChange = (newFilter) => {
        setFilter(newFilter);
    };

    const toggleSidebar = () => {
        setSidebarOpen(!sidebarOpen);
    };

    return (
        <div className="app-container">
            <Header toggleSidebar={toggleSidebar} />
            <div className="main-content">
                {/* üî• Sidebar con b√∫squeda y acciones */}
                <Sidebar
                    onFilterChange={handleFilterChange}
                    onSearch={handleSearch}
                    onMLAction={(action) => alert(`T√©cnica de ML seleccionada: ${action}`)}
                    isOpen={sidebarOpen}
                    closeSidebar={() => setSidebarOpen(false)}
                />

                <div className="content">
                    <div className="post">
                        <h3>Sobre el Proyecto</h3>
                        <p>
                            Este proyecto utiliza t√©cnicas de machine learning para identificar instrumentos musicales de la regi√≥n andina a partir de archivos de audio.
                            La identificaci√≥n de instrumentos musicales a partir de grabaciones de audio es una tarea
                            compleja que ha cobrado gran relevancia en el campo de la musicolog√≠a y las aplicaciones
                            tecnol√≥gicas. La creciente disponibilidad de grandes bases de datos de m√∫sica accesibles
                            al p√∫blico ha creado la necesidad de desarrollar sistemas que permitan la clasificaci√≥n
                            y el reconocimiento autom√°tico de los contenidos de estos archivos. La identificaci√≥n
                            de instrumentos musicales en grabaciones de audio es crucial para diversas aplicaciones,
                            tales como la indexaci√≥n autom√°tica de datos, la recuperaci√≥n de informaci√≥n en bases de
                            datos, el reconocimiento de estilos musicales, la edici√≥n de audio, la generaci√≥n de listas
                            de reproducci√≥n, la recuperaci√≥n de audio y la transcripci√≥n musical. A pesar de los
                            numerosos estudios propuestos sobre la identificaci√≥n de instrumentos musicales, no se ha
                            encontrado investigaci√≥n espec√≠fica sobre la identificaci√≥n de los instrumentos t√≠picos de la
                            regi√≥n andina
                        </p>
                    </div>

                    {/* üéº AudioUploader para subir y clasificar audio */}
                    <AudioUploader onInstrumentIdentified={setResults} />
                    {/* Mostrar resultados */}
                    {results && <Results results={results} />}
                    {identifiedInstrument && (
                        <InstrumentDisplay instrumentData={identifiedInstrument} />
                    )}

                    {/* üìú Secciones de historia con referencias */}
                    <div className="history-section">
                        <h3>Instrumentos de Viento</h3>
                        <div className="instrument-grid">
                            <div ref={refs.quena}>
                                <History instrument={{
                                    name: 'Quena',
                                    history: 'La quena es una flauta andina utilizada tradicionalmente en la m√∫sica de los Andes.',
                                    imageUrl: 'Quena01.jpg'
                                }} />
                            </div>
                            <div ref={refs.sikus}>
                                <History instrument={{
                                    name: 'Sikus',
                                    history: 'El siku o zampo√±a es un conjunto de flautas de pan hechas de ca√±a.',
                                    imageUrl: 'Sikus01.jpg'
                                }} />
                            </div>
                            <div ref={refs.toyo}>
                                <History instrument={{
                                    name: 'Toyos',
                                    history: 'El toyo es un gran conjunto de flautas de madera con un sonido grave.',
                                    imageUrl: 'Toyos01.jpg'
                                }} />
                            </div>
                            <div ref={refs.ocarina}>
                                <History instrument={{
                                    name: 'Ocarina',
                                    history: 'La ocarina es un instrumento de viento que produce sonido en una c√°mara ovalada.',
                                    imageUrl: 'Ocarina01.jpg'
                                }} />
                            </div>
                        </div>

                        <h3>Instrumentos de Cuerda</h3>
                        <div className="instrument-grid">
                            <div ref={refs.charango}>
                                <History instrument={{
                                    name: 'Charango',
                                    history: 'El charango es un instrumento de cuerdas utilizado en la m√∫sica tradicional de los Andes.',
                                    imageUrl: 'Charango01.jpg'
                                }} />
                            </div>
                            <div ref={refs.guitarra}>
                                <History instrument={{
                                    name: 'Guitarra',
                                    history: 'Una variante de la guitarra espa√±ola adaptada a la m√∫sica andina.',
                                    imageUrl: 'Guitarra01.jpg'
                                }} />
                            </div>
                            <div ref={refs.bandola}>
                                <History instrument={{
                                    name: 'Bandola',
                                    history: 'Un instrumento similar a la mandolina, com√∫n en la m√∫sica de los Andes.',
                                    imageUrl: 'Bandola01.jpg'
                                }} />
                            </div>
                            <div ref={refs.tiple}>
                                <History instrument={{
                                    name: 'Tiple',
                                    history: 'El tiple es un peque√±o instrumento de cuerda conocido por su sonido agudo.',
                                    imageUrl: 'Tiple01.jpg'
                                }} />
                            </div>
                        </div>

                        <h3>Instrumentos de Percusi√≥n</h3>
                        <div className="instrument-grid">
                            <div ref={refs.bombo}>
                                <History instrument={{
                                    name: 'Bombo',
                                    history: 'El bombo es un tambor grande utilizado en la m√∫sica andina.',
                                    imageUrl: 'Bombo01.jpg'
                                }} />
                            </div>
                            <div ref={refs.cajon}>
                                <History instrument={{
                                    name: 'Caj√≥n Peruano',
                                    history: 'El caj√≥n peruano es una caja de resonancia de madera originaria de Per√∫.',
                                    imageUrl: 'CajonPeruano01.jpg'
                                }} />
                            </div>
                            <div ref={refs.wankara}>
                                <History instrument={{
                                    name: 'Wankara',
                                    history: 'El wankara es un tambor peque√±o usado en ceremonias tradicionales.',
                                    imageUrl: 'Wankara01.jpg'
                                }} />
                            </div>
                            <div ref={refs.guacharaca}>
                                <History instrument={{
                                    name: 'Guacharaca',
                                    history: 'La guacharaca es un instrumento de percusi√≥n idi√≥fono de raspado.',
                                    imageUrl: 'Guacharaca01.jpg'
                                }} />
                            </div>
                        </div>
                    </div>

                    {/* Informaci√≥n adicional del instrumento identificado */}
                    {identifiedInstrument && (
                        <InstrumentInfo instrument={identifiedInstrument} />
                    )}

                    <FeedbackForm />
                </div>
            </div>
            <Footer />
        </div>
    );
};

export default App;
