// frontend/src/services/api.js
import axios from 'axios';
import config from '../Config/Config';

const api = axios.create({
    baseURL: config.apiUrl, // URL base del backend
});

export const uploadAudio = async (file) => {
    const formData = new FormData();
    formData.append('file', file);
    const response = await api.post('/api/upload/', formData);
    return response.data; // Devuelve el ID del archivo
};

export const identifyInstrument = async (audioId) => {
    const response = await api.get(`/api/identify/${audioId}/`);
    return response.data; // Devuelve el instrumento identificado y el espectrograma
};

export const getInstrumentInfo = async (instrumentId) => {
    const response = await api.get(`/api/instrument/${instrumentId}/`);
    return response.data; // Devuelve información del instrumento
};
