import React, { useState } from 'react';
import Header from './Components/Header';
import Sidebar from './Components/Sidebar';
import AudioUploader from './Components/AudioUploader';
import Results from './Components/Results';
import Footer from './Components/Footer';
import './App.css';

const App = () => {
    const [identifiedInstrument, setIdentifiedInstrument] = useState(null); // Resultado del backend

    // Manejar el resultado del backend tras analizar el audio
    const handleInstrumentIdentified = (instrument) => {
        setIdentifiedInstrument(instrument);
    };

    return (
        <div className="app-container">
            <Header />
            <div className="main-content">
                <Sidebar />
                <div className="content">
                    <div className="post">
                        <h3>Sobre el Proyecto</h3>
                        <p>Este proyecto utiliza técnicas de Machine Learning para identificar instrumentos musicales andinos a partir de archivos de audio.</p>
                    </div>

                    {/* Único componente de subida de audio */}
                    <AudioUploader onInstrumentIdentified={handleInstrumentIdentified} />

                    {/* Mostrar resultados si están disponibles */}
                    {identifiedInstrument ? (
                        <Results result={identifiedInstrument} />
                    ) : (
                        <p className="no-results">Sube un archivo para comenzar.</p>
                    )}
                </div>
            </div>
            <Footer />
        </div>
    );
};

export default App;
