// frontend/src/components/AudioUploader.js
import React, { useState } from 'react';
import { uploadAudio, identifyInstrument } from '../Services/Api';
import './AudioUploader.css';

const AudioUploader = ({ onInstrumentIdentified }) => {
    const [audioFile, setAudioFile] = useState(null);
    const [isUploading, setIsUploading] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');
    const [progress, setProgress] = useState(0); // Para barra de progreso

    // Manejar selección de archivo
    const handleFileChange = (e) => {
        const file = e.target.files[0];
        if (!file) {
            setErrorMessage('Selecciona un archivo.');
            return;
        }
        if (!file.type.startsWith('audio/')) {
            setErrorMessage('El archivo debe ser un archivo de audio válido.');
            return;
        }
        if (file.size > 10 * 1024 * 1024) { // Máximo 10 MB
            setErrorMessage('El archivo es demasiado grande (máximo 10 MB).');
            return;
        }
        setErrorMessage('');
        setAudioFile(file);
    };

    // Manejar subida y análisis
    const handleUpload = async () => {
        if (!audioFile) {
            setErrorMessage('Por favor selecciona un archivo antes de subir.');
            return;
        }

        setIsUploading(true);
        setErrorMessage('');
        try {
            const uploadResponse = await uploadAudio(audioFile);
            console.log('Audio uploaded:', uploadResponse);

            const audioId = uploadResponse.id; // Asegúrate de que el backend devuelve un `id` en el response
            const identifyResponse = await identifyInstrument(audioId);
            console.log('Instrument identified:', identifyResponse);

            onInstrumentIdentified(identifyResponse); // Pasar resultados al componente padre
        } catch (error) {
            console.error('Error subiendo o identificando el audio:', error);
            setErrorMessage('Error al procesar el archivo. Intenta nuevamente.');
        } finally {
            setIsUploading(false);
        }
    };

    return (
        <div className="audio-uploader">
            <input type="file" className="custom-file-input" onChange={handleFileChange} />
            <button className="andean-button" onClick={handleUpload} disabled={isUploading}>
                {isUploading ? 'Procesando...' : 'Clasificar'}
            </button>
            {progress > 0 && <progress value={progress} max="100" />}
            {errorMessage && <p className="error-message">{errorMessage}</p>}
        </div>
    );
};

export default AudioUploader;
