// frontend/src/App.js
// frontend/src/App.js
import React, { useState, useRef } from 'react';
import Header from './Components/Header';
import Sidebar from './Components/Sidebar';
import AudioUploader from './Components/AudioUploader';
import InstrumentDisplay from './Components/InstrumentDisplay';
import InstrumentInfo from './Components/InstrumentInfo';
import FeedbackForm from './Components/FeedbackForm';
import History from './Components/History';
import Results from './Components/Results';
import Footer from './Components/Footer';
import './App.css';

const App = () => {
    const [filter, setFilter] = useState('all'); // Estado para el filtro
    const [results, setResults] = useState(null); // Estado para los resultados
    const [identifiedInstrument, setIdentifiedInstrument] = useState(null); // Estado para el instrumento identificado
    const [sidebarOpen, setSidebarOpen] = useState(false); // Estado para la barra lateral
    const [filteredInstruments, setFilteredInstruments] = useState([]); // Estado para lista filtrada
    const [mlInfo, setMlInfo] = useState(null); // Estado para mostrar info de ML

    // 游댐 Referencias para hacer scroll autom치tico a cada instrumento
    const refs = {
        quena: useRef(null),
        sikus: useRef(null),
        toyo: useRef(null),
        ocarina: useRef(null),
        charango: useRef(null),
        guitarra: useRef(null),
        bandola: useRef(null),
        tiple: useRef(null),
        bombo: useRef(null),
        cajon: useRef(null),
        wankara: useRef(null),
        guacharaca: useRef(null),
    };

    // 游꿢 Estado para mostrar la lista de instrumentos filtrados
    const [filteredList, setFilteredList] = useState([]);
    const [selectedFilter, setSelectedFilter] = useState("all"); // Renombramos 'filter' a 'selectedFilter'

    // 游꿮 Filtrar y mostrar lista de instrumentos por tipo
    const handleFilterChange = (newFilter) => {
        setSelectedFilter(newFilter); // Actualizamos correctamente el filtro seleccionado

        if (newFilter === "all") {
            setFilteredList([]); // No mostrar lista si es "Todos"
        } else {
            // Filtramos los instrumentos seg칰n el tipo seleccionado
            const list = instruments
                .filter((inst) => inst.type === newFilter)
                .map((inst) => ({
                    name: inst.name,
                    key: inst.name.toLowerCase().replace(/\s+/g, ""), // Generamos clave segura
                }));
            setFilteredList(list);
        }
    };

    // 游늷 Funci칩n para mover a la secci칩n al hacer clic en un instrumento
    const scrollToInstrument = (key) => {
        if (refs[key]) {
            refs[key].current?.scrollIntoView({ behavior: 'smooth', block: 'center' });
        } else {
            alert(`No se encontr칩 la referencia para "${key}"`);
        }
    };

    // 游꿧 Lista de instrumentos con su categor칤a
    const instruments = [
        { name: "Quena", type: "viento", history: "Flauta andina tradicional", imageUrl: "Quena01.jpg" },
        { name: "Sikus", type: "viento", history: "Zampo침a hecha de ca침a", imageUrl: "Sikus01.jpg" },
        { name: "Toyos", type: "viento", history: "Flauta de madera con sonido grave", imageUrl: "Toyos01.jpg" },
        { name: "Ocarina", type: "viento", history: "Instrumento de viento con c치mara ovalada", imageUrl: "Ocarina01.jpg" },
