// frontend/src/App.js
import React, { useState } from 'react';
import Header from './Components/Header';
import Sidebar from './Components/Sidebar';
import AudioUploader from './Components/AudioUploader';
import InstrumentDisplay from './Components/InstrumentDisplay';
import InstrumentInfo from './Components/InstrumentInfo';
import FeedbackForm from './Components/FeedbackForm';
import History from './Components/History';
import Results from './Components/Results';
import Footer from './Components/Footer';
import './App.css';

const App = () => {
    const [filter, setFilter] = useState('all');
    const [results, setResults] = useState(null); // Estado para los resultados
    const [sidebarOpen, setSidebarOpen] = useState(false);

    const handleFilterChange = (newFilter) => {
        setFilter(newFilter);
    };

    const handleInstrumentIdentified = (data) => {
        setResults(data); // Almacenar resultados recibidos desde AudioUploader
    };

    const toggleSidebar = () => {
        setSidebarOpen(!sidebarOpen);
    };

    return (
        <div className="app-container">
            <Header toggleSidebar={toggleSidebar} />
            <div className="main-content">
                <Sidebar
                    onFilterChange={handleFilterChange}
                    isOpen={sidebarOpen}
                    closeSidebar={() => setSidebarOpen(false)}
                />
                <div className="content">
                    <div className="post">
                        <h3>Sobre el Proyecto</h3>
                        <p>
                            Este proyecto utiliza técnicas de machine learning para identificar instrumentos musicales de la región andina a partir de archivos de audio.
                            La identificación de instrumentos musicales a partir de grabaciones de audio es una tarea
                            compleja que ha cobrado gran relevancia en el campo de la musicología y las aplicaciones
                            tecnológicas.
                        </p>
                    </div>
                    <AudioUploader onInstrumentIdentified={handleInstrumentIdentified} />
                    {results && <Results results={results} />}
                    <div className="history-section">
                        <h3>Instrumentos de Viento</h3>
                        <div className="instrument-grid">
                            <History instrument={{ name: 'Quena', history: 'La quena es una flauta andina utilizada tradicionalmente en la música de los Andes.', imageUrl: 'Quena01.jpg' }} />
                            <History instrument={{ name: 'Sikus', history: 'El siku o zampoña es un conjunto de flautas de pan hechas de caña, con tubos de diferentes longitudes.', imageUrl: 'Sikus01.jpg' }} />
                        </div>
                    </div>
                    {results && <InstrumentInfo instrument={results.instrument} />}
                    <FeedbackForm />
                </div>
            </div>
            <Footer />
        </div>
    );
};

export default App;
