// frontend/src/components/Results.js
import React, { useState } from 'react';
import AudioUploader from './AudioUploader';
import './Results.css';

const Results = () => {
    const [activeTab, setActiveTab] = useState('upload');
    const [result, setResult] = useState(null);

    const handleResult = (data) => {
        setResult(data); // Almacena los resultados
        setActiveTab('results'); // Cambia a la pestaña de resultados
    };

    return (
        <div className="results">
            <h3>Clasificación de Instrumentos Andinos</h3>
            <div className="tabs">
                <button className={activeTab === 'upload' ? 'active' : ''} onClick={() => setActiveTab('upload')}>
                    Subir Audio
                </button>
                <button className={activeTab === 'results' ? 'active' : ''} onClick={() => setActiveTab('results')}>
                    Resultados
                </button>
            </div>
            {activeTab === 'upload' && <AudioUploader onInstrumentIdentified={handleResult} />}
            {activeTab === 'results' && result && (
                <div className="results-tab">
                    <h4>Instrumento Detectado</h4>
                    <p><strong>Instrumento:</strong> {result.instrument}</p>
                    <h5>Probabilidades:</h5>
                    <ul>
                        {result.probabilities.map((prob, index) => (
                            <li key={index}>
                                Instrumento {index + 1}: {(prob * 100).toFixed(2)}%
                            </li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default Results;
