// frontend/src/components/AudioUploader.js
import React, { useState } from 'react';
import { identifyInstrument } from '../Services/Api';
import { Bar } from 'react-chartjs-2';
import './AudioUploader.css';

const AudioUploader = () => {
    const [audioFile, setAudioFile] = useState(null);
    const [isUploading, setIsUploading] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');
    const [results, setResults] = useState(null);
    const [predictedInstrument, setPredictedInstrument] = useState('');

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        if (!file.type.startsWith('audio/')) {
            setErrorMessage('El archivo debe ser un archivo de audio válido.');
            return;
        }
        setErrorMessage('');
        setAudioFile(file);
    };

    const handleUpload = async () => {
        if (!audioFile) {
            setErrorMessage('Por favor selecciona un archivo antes de subir.');
            return;
        }

        setIsUploading(true);
        setErrorMessage('');

        const formData = new FormData();
        formData.append('file', audioFile);

        try {
            const response = await identifyInstrument(formData);
            console.log('Respuesta del servidor:', response);

            if (response.predicted_class && response.results) {
                setPredictedInstrument(response.predicted_class);
                setResults(response.results);
            } else {
                setErrorMessage('No se encontraron resultados en la respuesta del servidor.');
            }
        } catch (error) {
            console.error('Error al subir el archivo:', error);
            setErrorMessage('Hubo un error procesando el archivo.');
        } finally {
            setIsUploading(false);
        }
    };

    // Obtener las tres predicciones más probables
    const top3Results = results
        ? [...results].sort((a, b) => b.probability - a.probability).slice(0, 3)
        : [];

    return (
        <div className="audio-uploader">
            <input type="file" onChange={handleFileChange} />
            <button onClick={handleUpload} disabled={isUploading}>
                {isUploading ? 'Procesando...' : 'Subir y Clasificar'}
            </button>
            {errorMessage && <p className="error-message">{errorMessage}</p>}
            {predictedInstrument && (
                <h3>Instrumento Detectado: {predictedInstrument}</h3>
            )}
            {results && (
                <>
                    {/* Primera gráfica: todas las predicciones */}
                    <div className="chart-container">
                        <h3 style={{ color: '#8B4513', fontFamily: 'IM Fell DW Pica, serif' }}>
                            Gráfica de Probabilidades
                        </h3>
                        <Bar
                            data={{
                                labels: results.map((item) => item.instrument),
                                datasets: [
                                    {
                                        label: 'Probabilidad (%)',
                                        data: results.map((item) => item.probability * 100),
                                        backgroundColor: results.map((item) =>
                                            item.instrument === predictedInstrument ? 'green' : 'red'
                                        ),
                                    },
                                ],
                            }}
                            options={{
                                indexAxis: 'y',
                                responsive: true,
                                plugins: {
                                    legend: { display: false },
                                },
                                scales: {
                                    x: { beginAtZero: true, max: 100 },
                                    y: { ticks: { color: '#000000', font: { weight: 'bold' } } },
                                },
                            }}
                        />
                    </div>

                    {/* Segunda gráfica: Top 3 predicciones */}
                    <div className="chart-container">
                        <h3 style={{ color: '#8B4513', fontFamily: 'IM Fell DW Pica, serif' }}>
                            Top 3 Predicciones
                        </h3>
                        <Bar
                            data={{
                                labels: top3Results.map((item) => item.instrument),
                                datasets: [
                                    {
                                        label: 'Probabilidad (%)',
                                        data: top3Results.map((item) => item.probability * 100),
                                        backgroundColor: 'orange',
                                    },
                                ],
                            }}
                            options={{
                                responsive: true,
                                plugins: {
                                    legend: { display: false },
                                },
                                scales: {
                                    x: { beginAtZero: true, max: 100 },
                                    y: { ticks: { color: '#000000', font: { weight: 'bold' } } },
                                },
                            }}
                        />
                    </div>
                </>
            )}
        </div>
    );
};

export default AudioUploader;
