// frontend/src/components/AudioUploader.js
import React, { useState } from 'react';
import { identifyInstrument } from '../Services/Api'; // Cambia uploadAudio por identifyInstrument
import './AudioUploader.css';


const AudioUploader = ({ onInstrumentIdentified }) => {
    const [audioFile, setAudioFile] = useState(null); // Estado para almacenar el archivo seleccionado
    const [isUploading, setIsUploading] = useState(false); // Estado para indicar si se está subiendo el archivo
    const [errorMessage, setErrorMessage] = useState(''); // Estado para manejar mensajes de error

    // Función para manejar el cambio de archivo
    const handleFileChange = (e) => {
        const file = e.target.files[0];
        if (!file) {
            setErrorMessage('Por favor selecciona un archivo válido.');
            return;
        }
        if (!file.type.startsWith('audio/')) {
            setErrorMessage('El archivo debe ser un archivo de audio válido.');
            return;
        }
        setErrorMessage(''); // Reinicia cualquier mensaje de error
        setAudioFile(file); // Almacena el archivo seleccionado
    };

    // Función para manejar la subida del archivo
    const handleUpload = async () => {
        if (!audioFile) {
            setErrorMessage('Por favor selecciona un archivo antes de subir.');
            return;
        }

        const formData = new FormData();
        formData.append('file', audioFile);

        console.log('Archivo seleccionado para enviar:', audioFile);
        console.log('FormData preparado:', formData.get('file')); // Verifica que el archivo esté correctamente cargado

        setIsUploading(true);
        setErrorMessage('');
        try {
            const response = await identifyInstrument(formData); // Llama al backend
            console.log('Respuesta del servidor:', response);
            onInstrumentIdentified(response.data); // Envía los datos al componente padre
        } catch (error) {
            console.error('Error al subir el archivo:', error);
            setErrorMessage('Hubo un error procesando el archivo.');
        } finally {
            setIsUploading(false);
        }
    };


    return (
        <div className="audio-uploader">
            <input type="file" onChange={handleFileChange} /> {/* Llama a handleFileChange cuando se selecciona un archivo */}
            <button onClick={handleUpload} disabled={isUploading}>
                {isUploading ? 'Procesando...' : 'Subir y Clasificar'}
            </button>
            {errorMessage && <p className="error-message">{errorMessage}</p>}
        </div>
    );
};

export default AudioUploader;
