// frontend/src/components/AudioUploader.js
import React, { useState } from 'react';
import { uploadAudio, identifyInstrument } from '../services/api';
import './AudioUploader.css';

const AudioUploader = ({ onResult }) => {
    const [audioFile, setAudioFile] = useState(null);
    const [isLoading, setIsLoading] = useState(false);

    const handleFileChange = (e) => {
        setAudioFile(e.target.files[0]);
    };

    const handleUpload = async () => {
        if (!audioFile) {
            alert('Por favor, selecciona un archivo de audio.');
            return;
        }

        setIsLoading(true);
        try {
            // Subir el archivo de audio
            const uploadResponse = await uploadAudio(audioFile);
            const audioId = uploadResponse.data.id;

            // Identificar el instrumento
            const identifyResponse = await identifyInstrument(audioId);
            onResult(identifyResponse.data); // Pasar los resultados al componente padre
        } catch (error) {
            console.error('Error al subir o identificar el archivo:', error);
            alert('Ocurrió un error al procesar el archivo.');
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="audio-uploader">
            <input
                type="file"
                className="custom-file-input"
                onChange={handleFileChange}
                accept="audio/*"
            />
            <button className="andean-button" onClick={handleUpload} disabled={isLoading}>
                {isLoading ? 'Procesando...' : 'Clasificar'}
            </button>
        </div>
    );
};

export default AudioUploader;
