import React, { useState } from 'react';
import './Sidebar.css';

const Sidebar = ({ onFilterChange, onSearch }) => {
    const [searchQuery, setSearchQuery] = useState('');
    const [expandedSection, setExpandedSection] = useState(null);

    // 🎯 Función para mostrar/ocultar la descripción
    const toggleSection = (section) => {
        setExpandedSection(expandedSection === section ? null : section);
    };

    return (
        <div className="sidebar" style={{ width: '25%' }}>
            {/* 🔍 Sección de búsqueda */}
            <div className="search-section">
                <input
                    type="text"
                    placeholder="Buscar instrumento..."
                    className="search-input"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                />
                <button
                    className="andean-button"
                    onClick={() => onSearch(searchQuery)}
                >
                    Buscar
                </button>
            </div>

            {/* 🎼 Filtrar por tipo */}
            <div className="filters-section">
                <h3>Filtrar por tipo:</h3>
                <select onChange={(e) => onFilterChange(e.target.value)}>
                    <option value="all">Todos</option>
                    <option value="viento">Viento</option>
                    <option value="cuerda">Cuerda</option>
                    <option value="percusion">Percusión</option>
                </select>
            </div>

            {/* 🤖 Sección de Técnicas de ML con expansión */}
            <div className="ml-techniques-section">
                <h3>Técnicas de ML</h3>

                <button className="andean-button" onClick={() => toggleSection("clasificacion")}>
                    Clasificación
                </button>
                {expandedSection === "clasificacion" && (
                    <div className="expanded-section">
                        🔎 <strong>Clasificación:</strong> Un algoritmo analiza el audio y determina a qué categoría pertenece el instrumento.
                    </div>
                )}

                <button className="andean-button" onClick={() => toggleSection("identificacion")}>
                    Identificación
                </button>
                {expandedSection === "identificacion" && (
                    <div className="expanded-section">
                        🎼 <strong>Identificación:</strong> Se detecta la presencia de un instrumento específico en una grabación de audio.
                    </div>
                )}
            </div>
        </div>
    );
};

export default Sidebar;
