// frontend/src/services/api.js
import axios from 'axios';
import config from '../Config/Config';

const api = axios.create({
    baseURL: config.apiUrl, // URL del backend
});

// Subir un archivo de audio al backend
export const uploadAudio = async (file) => {
    const formData = new FormData();
    formData.append('file', file);
    const response = await api.post('/upload/', formData);
    return response.data; // Devuelve el ID del archivo subido
};

// Identificar el instrumento basado en el ID del archivo de audio
export const identifyInstrument = async (audioId) => {
    const response = await api.get(`/identify/${audioId}/`);
    return response.data; // Devuelve los resultados del análisis
};
