// frontend/src/components/Results.js
import React from 'react';
import './Results.css';

const Results = ({ results }) => {
    if (!results) return null;

    return (
        <div className="results">
            <h3>Resultados del An√°lisis</h3>
            <p><strong>Instrumento Detectado:</strong> {results.instrument}</p>
            <h4>Probabilidades:</h4>
            <ul>
                {results.probabilities.map((prob, index) => (
                    <li key={index}>
                        Instrumento {index + 1}: {(prob * 100).toFixed(2)}%
                    </li>
                ))}
            </ul>
            <h4>Espectrograma:</h4>
            <img src={`data:image/png;base64,${results.spectrogram}`} alt="Espectrograma" />
        </div>
    );
};

export default Results;
